import React, { useState, useEffect, useRef } from "react";
import { Link, Outlet } from "react-router-dom";
import { HomeIcon, ChartBarIcon, CogIcon, UsersIcon, BellIcon, LogoutIcon, ChevronLeftIcon, ChevronRightIcon } from "@heroicons/react/solid";

export default function Nav() {
  const [active, setActive] = useState("Dashboard");
  const [collapsed, setCollapsed] = useState(window.innerWidth < 768);
  const [isDropdownVisible, setIsDropdownVisible] = useState(false);
  const dropdownRef = useRef(null);

  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth < 768) {
        setCollapsed(true);
      }
    };
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Close dropdown when clicking outside
  useEffect(() => {
    function handleClickOutside(event) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownVisible(false);
      }
    }
    if (isDropdownVisible) {
      document.addEventListener("mousedown", handleClickOutside);
    }
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [isDropdownVisible]);

  return (
    <div className="flex h-screen bg-gray-100 overflow-hidden">
      <aside
        className={`bg-grayish text-white flex flex-col p-4 relative transition-all duration-500 ease-in-out ${
          collapsed ? "w-20" : "w-64"
        }`}
      >
        <button
          className="absolute top-12 right-[-12px] p-1 bg-red-600 text-white rounded-full hover:bg-red-700 transition-all duration-300 ease-in-out shadow-lg"
          onClick={() => setCollapsed(!collapsed)}
        >
          {collapsed ? <ChevronRightIcon className="w-5 h-5" /> : <ChevronLeftIcon className="w-5 h-5" />}
        </button>

        <div className={`flex items-center justify-center mb-10 mt-5 transition-all duration-500 ease-in-out ${collapsed ? "opacity-100" : "opacity-100"}`}>
          <img src="/images/tupseal.png" alt="Tupseal Logo" className={`transition-all duration-500 ease-in-out ${collapsed ? "w-12 h-12" : "w-32 h-32"}`} />
        </div>

        <nav className="flex flex-col space-y-2">
          {[
            { name: "Dashboard", icon: HomeIcon, path: "/dashboard" },
            { name: "Usage Monitor", icon: ChartBarIcon, path: "/usage-monitor" },
            { name: "Janitors", icon: UsersIcon, path: "/janitors" },
            { name: "Resources", icon: CogIcon, path: "/resources" },
            { name: "Settings", icon: CogIcon, path: "/settings" },
            { name: "Users", icon: UsersIcon, path: "/users" },
          ].map((item) => (
            <Link
              to={item.path}
              key={item.name}
              className={`flex items-center p-3 rounded-lg transition-all duration-500 ease-in-out ${
                active === item.name ? "bg-gray-1 text-grayish" : "hover:bg-gray-1-500"
              } hover:scale-105 ${collapsed ? "justify-start" : ""}`}
              onClick={() => setActive(item.name)}
            >
              <item.icon className="w-6 h-6 flex-shrink-0 transition-all duration-500 ease-in-out" />
              <span className={`whitespace-nowrap overflow-hidden transition-all duration-500 ease-in-out ${collapsed ? "opacity-0 w-0" : "opacity-100 w-auto ml-3"}`}>
                {item.name}
              </span>
            </Link>
          ))}
        </nav>

        <button
          className="mt-auto flex items-center p-3 rounded-lg hover:bg-red-700 transition-all duration-500 ease-in-out transform hover:scale-105"
          onClick={() => console.log("Logout clicked")}
        >
          <LogoutIcon className={`w-6 h-6 transition-all duration-500 ease-in-out ${collapsed ? "mr-0" : "mr-3"}`} />
          <span className={`whitespace-nowrap overflow-hidden transition-all duration-500 ease-in-out ${collapsed ? "opacity-0 w-0" : "opacity-100 w-auto"}`}>Log Out</span>
        </button>
      </aside>

      <div className="flex-1 flex flex-col w-full">
        <header className="bg-fafbfe shadow-md p-4 flex justify-between items-center w-full md:px-6">
          <div className="flex items-center space-x-4">
            <div className="relative" ref={dropdownRef}>
              <BellIcon
                className="w-6 h-6 text-gray-600 cursor-pointer hover:text-gray-800 transition-colors duration-300 hover:scale-110"
                onClick={() => setIsDropdownVisible(!isDropdownVisible)}
              />
              {isDropdownVisible && (
                <div className="absolute right-0 shadow-lg bg-white py-4 z-[1000] min-w-full rounded-lg w-[410px] max-h-[500px] overflow-auto mt-2">
                  <div className="flex items-center justify-between px-4 mb-4">
                    <p className="text-xs text-blue-600 cursor-pointer">Clear all</p>
                    <p className="text-xs text-blue-600 cursor-pointer">Mark as read</p>
                  </div>

                  <ul className="divide-y">
                    {[
                      { name: "Yin", time: "10 minutes ago", img: "https://readymadeui.com/profile_2.webp" },
                      { name: "Haper", time: "2 hours ago", img: "https://readymadeui.com/team-2.webp" },
                      { name: "San", time: "1 day ago", img: "https://readymadeui.com/team-3.webp" },
                      { name: "Seeba", time: "30 minutes ago", img: "https://readymadeui.com/team-4.webp" },
                    ].map((notif, index) => (
                      <li key={index} className="p-4 flex items-center hover:bg-gray-50 cursor-pointer">
                        <img src={notif.img} alt={`${notif.name}'s Avatar`} className="w-12 h-12 rounded-full shrink-0" />
                        <div className="ml-6">
                          <h3 className="text-sm text-[#333] font-semibold">You have a new message from {notif.name}</h3>
                          <p className="text-xs text-gray-500 mt-2">Hello there, check this new item in from the motion school.</p>
                          <p className="text-xs text-blue-600 leading-3 mt-2">{notif.time}</p>
                        </div>
                      </li>
                    ))}
                  </ul>
                  <p className="text-xs px-4 mt-6 mb-4 inline-block text-blue-600 cursor-pointer">View all Notifications</p>
                </div>
              )}
            </div>
            <div className="flex items-center">
              <img className="w-8 h-8 rounded-full border object-cover transition-transform duration-300 hover:scale-110" src="/images/bongbong.jpg" alt="User Avatar" />
              <span className="ml-2 mr-10 font-medium hidden sm:block">@juandelacruz</span>
            </div>
          </div>
        </header>

        <main className="p-4 md:p-6 bg-gray-100 flex-1 overflow-y-auto">
          <Outlet />
        </main>
      </div>
    </div>
  );
}
