import React, { useState, useEffect, useRef } from "react";
import { Link, Outlet } from "react-router-dom";
import { FixedSizeList as List } from "react-window";
import {
  HomeIcon, ChartBarIcon, CogIcon, UsersIcon, BellIcon, LogoutIcon,
  ChevronLeftIcon, ChevronRightIcon,
} from "@heroicons/react/solid";

export default function Nav({ navItems, notifications, userProfile }) {
  const [active, setActive] = useState("Dashboard");
  const [collapsed, setCollapsed] = useState(window.innerWidth < 768);
  const [isDropdownVisible, setIsDropdownVisible] = useState(false);
  const dropdownRef = useRef(null);

  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth < 768) {
        setCollapsed(true);
      }
    };
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownVisible(false);
      }
    };
    if (isDropdownVisible) {
      document.addEventListener("mousedown", handleClickOutside);
    } else {
      document.removeEventListener("mousedown", handleClickOutside);
    }
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [isDropdownVisible]);

  useEffect(() => {
    const handleKeyDown = (event) => {
      if (isDropdownVisible && event.key === "Escape") {
        setIsDropdownVisible(false);
      }
    };
    document.addEventListener("keydown", handleKeyDown);
    return () => document.removeEventListener("keydown", handleKeyDown);
  }, [isDropdownVisible]);

  return (
    <div className="flex h-screen bg-gray-100 overflow-hidden">
      {/* Sidebar */}
      <aside
        className={`bg-grayish text-white flex flex-col p-4 relative transition-all duration-500 ease-in-out ${
          collapsed ? "w-20" : "w-64"
        }`}
      >
        {/* Toggle Button */}
        <button
          aria-label={collapsed ? "Expand Sidebar" : "Collapse Sidebar"}
          className="absolute top-12 right-[-12px] p-1 bg-red-600 text-white rounded-full hover:bg-red-700 transition-all duration-300 ease-in-out shadow-lg"
          onClick={() => setCollapsed(!collapsed)}
        >
          {collapsed ? <ChevronRightIcon className="w-5 h-5" /> : <ChevronLeftIcon className="w-5 h-5" />}
        </button>

        {/* Logo */}
        <div className={`flex items-center justify-center mb-10 mt-5 transition-all duration-500 ease-in-out`}>
          <img
            src="/images/tupseal.png"
            alt="Tupseal Logo"
            className={`transition-all duration-500 ease-in-out ${collapsed ? "w-12 h-12" : "w-32 h-32"}`}
            onError={(e) => {
              e.target.src = "/images/fallback-logo.png";
            }}
          />
        </div>

        {/* Navigation */}
        <nav className="flex flex-col space-y-2">
          {navItems.map((item) => (
            <Link
              to={item.path}
              key={item.name}
              className={`flex items-center p-3 rounded-lg transition-all duration-500 ease-in-out ${
                active === item.name ? "bg-gray-1 text-grayish" : "hover:bg-gray-1-500"
              } hover:scale-105 ${collapsed ? "justify-start" : ""}`}
              onClick={() => setActive(item.name)}
            >
              <item.icon className="w-6 h-6 flex-shrink-0 transition-all duration-500 ease-in-out" />
              <span
                className={`whitespace-nowrap overflow-hidden transition-all duration-500 ease-in-out ${
                  collapsed ? "opacity-0 w-0" : "opacity-100 w-auto ml-3"
                }`}
              >
                {item.name}
              </span>
            </Link>
          ))}
        </nav>

        {/* Logout */}
        <button
          aria-label="Log Out"
          className="mt-auto flex items-center p-3 rounded-lg hover:bg-red-700 transition-all duration-500 ease-in-out transform hover:scale-105"
          onClick={() => console.log("Logout clicked")}
        >
          <LogoutIcon className={`w-6 h-6 transition-all duration-500 ease-in-out ${collapsed ? "mr-0" : "mr-3"}`} />
          <span className={`whitespace-nowrap overflow-hidden transition-all duration-500 ease-in-out ${collapsed ? "opacity-0 w-0" : "opacity-100 w-auto"}`}>Log Out</span>
        </button>
      </aside>

      {/* Main Content */}
      <div className="flex-1 flex flex-col w-full">
        {/* Navbar */}
        <header className="bg-fafbfe shadow-md p-4 flex justify-between items-center w-full md:px-6">
          <span className="text-lg font-semibold"></span>

          {/* Right Side Icons */}
          <div className="flex items-center space-x-4 ml-auto">
            {/* Notification Bell */}
            <div className="relative" ref={dropdownRef}>
              <button
                aria-label="Notifications"
                onClick={() => setIsDropdownVisible(!isDropdownVisible)}
              >
                <BellIcon className="w-6 h-6 text-gray-600 cursor-pointer hover:text-gray-800 transition-colors duration-300 hover:scale-110" />
              </button>
              {isDropdownVisible && (
                <div className='absolute right-0 shadow-lg bg-white py-4 z-[1000] min-w-full rounded-lg w-[410px] max-h-[500px] overflow-auto mt-2'>
                  <div className="flex items-center justify-between px-4 mb-4">
                    <p className="text-xs text-blue-600 cursor-pointer">Clear all</p>
                    <p className="text-xs text-blue-600 cursor-pointer">Mark as read</p>
                  </div>
                  <List
                    height={400}
                    itemCount={notifications.length}
                    itemSize={100}
                    width={400}
                  >
                    {({ index, style }) => (
                      <div style={style} className='p-4 flex items-center hover:bg-gray-50 cursor-pointer'>
                        <img
                          src={notifications[index].avatar}
                          alt={`${notifications[index].name}'s Avatar`}
                          className="w-12 h-12 rounded-full shrink-0"
                          onError={(e) => {
                            e.target.src = "/images/fallback-avatar.png";
                          }}
                        />
                        <div className="ml-6">
                          <h3 className="text-sm text-[#333] font-semibold">You have a new message from {notifications[index].name}</h3>
                          <p className="text-xs text-gray-500 mt-2">{notifications[index].message}</p>
                          <p className="text-xs text-blue-600 leading-3 mt-2">{notifications[index].time}</p>
                        </div>
                      </div>
                    )}
                  </List>
                  <p className="text-xs px-4 mt-6 mb-4 inline-block text-blue-600 cursor-pointer">View all Notifications</p>
                </div>
              )}
            </div>

            {/* User Profile */}
            <div className="flex items-center">
              <img
                className="w-8 h-8 rounded-full border object-cover transition-transform duration-300 hover:scale-110"
                src={userProfile.avatar}
                alt="User Avatar"
                onError={(e) => {
                  e.target.src = "/images/fallback-avatar.png";
                }}
              />
              <span className="ml-2 mr-8 font-medium hidden sm:block">{userProfile.username}</span>
            </div>
          </div>
        </header>

        {/* Main Content Area */}
        <main className="p-4 md:p-6 bg-gray-100 flex-1 overflow-y-auto">
          <Outlet />
        </main>
      </div>
    </div>
  );
}
