import react from "react";
import React, { useState } from "react";
import { HomeIcon, ChartBarIcon, UsersIcon, CogIcon } from "@heroicons/react/24/solid";
import { ArrowLeftIcon, ArrowRightIcon } from "@heroicons/react/24/solid";

const Sidebar = () => {
  const [active, setActive] = useState("Dashboard");
  const [isCollapsed, setIsCollapsed] = useState(false);

  const toggleSidebar = () => {
    setIsCollapsed(!isCollapsed);
  };

  return (
    <div className="flex flex-col h-screen bg-gray-100 p-4">
      {/* Collapse Button */}
      <button
        onClick={toggleSidebar}
        className="w-10 h-10 bg-red-500 rounded-full flex items-center justify-center self-end mb-4"
      >
        {isCollapsed ? (
          <ArrowRightIcon className="w-6 h-6 text-white" />
        ) : (
          <ArrowLeftIcon className="w-6 h-6 text-white" />
        )}
      </button>

      {/* Navigation */}
      <nav className="flex flex-col space-y-2">
        {[
          { name: "Dashboard", icon: HomeIcon },
          { name: "Usage Monitor", icon: ChartBarIcon },
          { name: "Janitors", icon: UsersIcon },
          { name: "Resources", icon: CogIcon },
          { name: "Settings", icon: CogIcon },
          { name: "Users", icon: UsersIcon },
        ].map((item) => (
          <button
            key={item.name}
            className={`flex items-center p-3 rounded-lg transition ${
              active === item.name
                ? "bg-gray-1 text-grayish"
                : "text-black hover:bg-gray-1-800"
            }`}
            onClick={() => setActive(item.name)}
          >
            <item.icon
              className={`w-6 h-6 ${isCollapsed ? "" : "mr-3"} ${
                active === item.name ? "text-grayish" : "text-black"
              }`}
            />
            {!isCollapsed && <span>{item.name}</span>}
          </button>
        ))}
      </nav>
    </div>
  );
};

export default Sidebar;
