import react from "react";
import React, { useState } from "react";
import {
  HomeIcon,
  ChartBarIcon,
  CogIcon,
  UsersIcon,
  BellIcon,
  LogoutIcon,
  ChevronLeftIcon,
  ChevronRightIcon,
} from "@heroicons/react/solid";

export default function DashboardLayout() {
  const [active, setActive] = useState("Dashboard");
  const [collapsed, setCollapsed] = useState(false);

  return (
    <div className="flex h-screen bg-gray-100 overflow-hidden">
      {/* Sidebar */}
      <aside
        className={`bg-grayish text-white flex flex-col p-4 relative transition-all duration-300 ease-in-out ${
          collapsed ? "w-20" : "w-64"
        }`}
      >
        {/* Toggle Button */}
        <button
          className="absolute mt-11 top-10 -right-4 p-1 bg-red-600 text-white rounded-full hover:bg-red-700 transition-all duration-300 ease-in-out shadow-lg"
          onClick={() => setCollapsed(!collapsed)}
        >
          {collapsed ? (
            <ChevronRightIcon className="w-5 h-5" /> // Smaller icon size
          ) : (
            <ChevronLeftIcon className="w-5 h-5" /> // Smaller icon size
          )}
        </button>

        {/* Logo */}
        <div
          className={`flex items-center justify-center mb-10 mt-8 transition-all duration-300 ease-in-out ${
            collapsed ? "opacity-100" : "opacity-100"
          }`}
        >
          <img
            src="/images/tupseal.png"
            alt="Tupseal"
            className={`transition-all duration-300 ease-in-out ${
              collapsed ? "w-12 h-12" : "w-32 h-32"
            }`}
          />
        </div>

        {/* Navigation */}
        <nav className="flex flex-col space-y-2">
          {[
            { name: "Dashboard", icon: HomeIcon },
            { name: "Usage Monitor", icon: ChartBarIcon },
            { name: "Janitors", icon: UsersIcon },
            { name: "Resources", icon: CogIcon },
            { name: "Settings", icon: CogIcon },
            { name: "Users", icon: UsersIcon },
          ].map((item) => (
            <button
              key={item.name}
              className={`flex items-center p-3 rounded-lg transition-all duration-300 ease-in-out ${
                active === item.name
                  ? "bg-gray-1"
                  : "hover:bg-gray-1-500 hover:scale-105"
              }`}
              onClick={() => setActive(item.name)}
            >
              <item.icon
                className={`w-6 h-6 ${
                  collapsed ? "mx-auto" : "mr-3"
                } ${
                  active === item.name ? "text-grayish" : "text-white"
                }`}
              />
              {!collapsed && (
                <span className={active === item.name ? "text-grayish" : ""}>
                  {item.name}
                </span>
              )}
            </button>
          ))}
        </nav>

        {/* Logout */}
        <button className="mt-auto flex items-center p-3 rounded-lg hover:bg-red-700 hover:scale-105 transition-all duration-300 ease-in-out">
          <LogoutIcon
            className={`w-6 h-6 ${collapsed ? "mx-auto" : "mr-3"}`}
          />
          {!collapsed && <span>Log Out</span>}
        </button>
      </aside>

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Navbar */}
        <header className="bg-white shadow-md p-4 flex justify-between items-center transition-shadow duration-300 ease-in-out">
          <h1 className="text-lg font-semibold">{active}</h1>
          <div className="flex items-center space-x-4">
            <BellIcon className="w-6 h-6 text-gray-600 cursor-pointer hover:text-gray-800 transition-colors duration-300" />
            <div className="flex items-center">
              <img
                className="w-8 h-8 rounded-full border transition-transform duration-300 hover:scale-110"
                src="https://via.placeholder.com/40"
                alt="User"
              />
              <span className="ml-2 font-medium">@juandelacruz</span>
            </div>
          </div>
        </header>

        {/* Main Content Area */}
        <main className="p-6 bg-gray-100 flex-1">
          <p>Dashboard content goes here...</p>
        </main>
      </div>
    </div>
  );
}
